                    // 5. Send a notification to the owner
                    const notificationData = {
                        title: "ÿ™ÿ±ŸÇŸäÿ© ÿßŸÑÿßÿπŸÑÿßŸÜ",
                        message: `ÿ™ŸÖÿ™ ÿ™ÿ±ŸÇŸäÿ© ÿßÿπŸÑÿßŸÜŸÉ ÿ±ŸÇŸÖ ${placeId} ŸÑŸÖÿØÿ© ${arabicTime}`,
                        redirectId: placeId,
                        redirectType: "post",
                        userId: ownerId, // ID of the user receiving the notification
