import { useState, useEffect } from 'react'
import { inventoryAPI } from '../../services/api'
import { format } from 'date-fns'
import { 
  ArrowTrendingUpIcon, 
  ArrowTrendingDownIcon,
  FunnelIcon
} from '@heroicons/react/24/outline'
import toast from 'react-hot-toast'

function InventoryHistoryTab() {
  const [history, setHistory] = useState([])
  const [loading, setLoading] = useState(true)
  const [filterType, setFilterType] = useState('all')

  useEffect(() => {
    loadHistory()
  }, [filterType])

  const loadHistory = async () => {
    try {
      setLoading(true)
      const params = filterType !== 'all' ? { type: filterType } : {}
      const response = await inventoryAPI.getInventoryHistory(params)

      if (response.data.success) {
        setHistory(response.data.history || [])
      }
    } catch (error) {
      toast.error('Failed to load inventory history')
      console.error('History load error:', error)
    } finally {
      setLoading(false)
    }
  }

  const formatDate = (dateString) => {
    try {
      return format(new Date(dateString), 'MMM d, yyyy HH:mm')
    } catch {
      return dateString
    }
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="loading-spinner mb-4"></div>
          <p className="text-gray-600">Loading history...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-4">
      {/* Filter */}
      <div className="flex items-center gap-4">
        <FunnelIcon className="h-5 w-5 text-gray-500" />
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          className="input"
        >
          <option value="all">All Transactions</option>
          <option value="manual">Manual Adjustments</option>
          <option value="order">Order Deductions</option>
        </select>
      </div>

      {/* History Table */}
      <div className="card">
        <div className="card-body">
          {history.length === 0 ? (
            <div className="text-center py-8">
              <p className="text-gray-500">No history found</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Item
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Change
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Type
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Reason
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      User
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {history.map((record) => (
                    <tr key={record.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {formatDate(record.created_at)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            {record.stock_item_name || 'Unknown'}
                          </div>
                          <div className="text-sm text-gray-500">
                            {record.sku || 'No SKU'}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          {parseFloat(record.change) > 0 ? (
                            <ArrowTrendingUpIcon className="h-4 w-4 text-green-500 mr-1" />
                          ) : (
                            <ArrowTrendingDownIcon className="h-4 w-4 text-red-500 mr-1" />
                          )}
                          <span className={`text-sm font-semibold ${
                            parseFloat(record.change) > 0 ? 'text-green-600' : 'text-red-600'
                          }`}>
                            {parseFloat(record.change) > 0 ? '+' : ''}
                            {record.change} {record.unit}
                          </span>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`badge ${
                          record.type === 'order' 
                            ? 'bg-blue-100 text-blue-800' 
                            : 'bg-gray-100 text-gray-800'
                        }`}>
                          {record.type === 'order' ? 'Order' : 'Manual'}
                        </span>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900 max-w-xs truncate">
                        {record.reason}
                        {record.order_code && (
                          <span className="ml-2 text-blue-600">
                            ({record.order_code})
                          </span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {record.full_name || record.user_name || 'System'}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  )
}

export default InventoryHistoryTab

frontend/src/pages/admin/RecipesManageme.content:
import { useState, useEffect } from 'react'
import { inventoryAPI, menuAPI } from '../../services/api'
import { PlusIcon, TrashIcon } from '@heroicons/react/24/outline'
import RecipeForm from '../../components/RecipeForm'
import toast from 'react-hot-toast'

function RecipesManagementTab() {
  const [recipes, setRecipes] = useState([])
  const [menuItems, setMenuItems] = useState([])
  const [loading, setLoading] = useState(true)
  const [showRecipeForm, setShowRecipeForm] = useState(false)
  const [groupedRecipes, setGroupedRecipes] = useState({})

  useEffect(() => {
    loadData()
  }, [])

  const loadData = async () => {
    try {
      setLoading(true)
      const [recipesRes, menuRes] = await Promise.all([
        inventoryAPI.getRecipes(),
        menuAPI.getMenuItems()
      ])

      if (recipesRes.data) {
        const recipesData = recipesRes.data.recipes || []
        setRecipes(recipesData)

        // Group recipes by menu item
        const grouped = recipesData.reduce((acc, recipe) => {
          const menuItemId = recipe.menu_item_id
          if (!acc[menuItemId]) {
            acc[menuItemId] = {
              menuItem: recipe.menu_item_name,
              ingredients: []
            }
          }
          acc[menuItemId].ingredients.push(recipe)
          return acc
        }, {})
        setGroupedRecipes(grouped)
      }

      if (menuRes.data.success) {
        setMenuItems(menuRes.data.items || [])
      }
    } catch (error) {
      toast.error('Failed to load recipes')
      console.error('Recipes load error:', error)
    } finally {
      setLoading(false)
    }
  }

  const handleDeleteRecipe = async (recipeId) => {
    if (!window.confirm('Are you sure you want to delete this recipe ingredient?')) {
      return
    }

    try {
      await inventoryAPI.deleteRecipe(recipeId)
      toast.success('Recipe ingredient deleted')
      loadData()
    } catch (error) {
      console.error('Delete recipe error:', error)
      toast.error('Failed to delete recipe ingredient')
    }
  }

  const handleSaveRecipe = () => {
    setShowRecipeForm(false)
    loadData()
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="loading-spinner mb-4"></div>
          <p className="text-gray-600">Loading recipes...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <p className="text-sm text-gray-600">
          Link menu items with their ingredients to enable automatic inventory deduction
        </p>
        <button
          onClick={() => setShowRecipeForm(true)}
          className="btn-primary"
        >
          <PlusIcon className="h-5 w-5 mr-2" />
          Add Recipe
        </button>
      </div>

      {Object.keys(groupedRecipes).length === 0 ? (
        <div className="card">
          <div className="card-body text-center py-12">
            <p className="text-gray-500 mb-4">No recipes configured yet</p>
            <button
              onClick={() => setShowRecipeForm(true)}
              className="btn-primary"
            >
              Create First Recipe
            </button>
          </div>
        </div>
      ) : (
        <div className="space-y-4">
          {Object.entries(groupedRecipes).map(([menuItemId, data]) => (
            <div key={menuItemId} className="card">
              <div className="card-header">
                <h3 className="text-lg font-semibold text-gray-900">
                  {data.menuItem}
                </h3>
              </div>
              <div className="card-body">
                <div className="space-y-2">
                  {data.ingredients.map((recipe) => (
                    <div 
                      key={recipe.id}
                      className="flex justify-between items-center p-3 bg-gray-50 rounded-lg"
                    >
                      <div className="flex-1">
                        <span className="text-sm font-medium text-gray-900">
                          {recipe.stock_item_name}
                        </span>
                        <span className="mx-2 text-gray-400">‚Üí</span>
                        <span className="text-sm text-gray-700">
                          {recipe.qty_per_serving} {recipe.unit} per serving
                        </span>
                        {recipe.stock_item_sku && (
                          <span className="ml-2 text-xs text-gray-500">
                            (SKU: {recipe.stock_item_sku})
                          </span>
                        )}
                      </div>
                      <button
                        onClick={() => handleDeleteRecipe(recipe.id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <TrashIcon className="h-4 w-4" />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Recipe Form Modal */}
      {showRecipeForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-gray-900">
                  Add Recipe
                </h2>
                <button
                  onClick={() => setShowRecipeForm(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <span className="sr-only">Close</span>
                  <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <RecipeForm
                onSave={handleSaveRecipe}
                onCancel={() => setShowRecipeForm(false)}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default RecipesManagementTab

